import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import SweetAlert from 'sweetalert2-react';
import { signUpStart } from '../../redux/user/user.actions';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import './sign-up.styles.scss';
import { createStructuredSelector } from 'reselect';
import { selectedUserError } from '../../redux/user/user.selectors';

const SignUp = ({ userError,signUpStart }) => {
    const [userDetails, setUserDetails] = useState({
        displayName: '',
        email: '',
        password: '',
        confirmPassword: '',
        error: '',
        showError: false,
    })

    const { displayName, email, password, confirmPassword, error, showError } = userDetails;

    useEffect(() => {
        // const { userError } = nextProps;

        if(userError) {
            setUserDetails({ ...userDetails, error: userError.message, showError: true });
        }
    } ,[displayName, email, password,confirmPassword, error, showError])

    const handleSubmit = async e => {
        e.preventDefault();

        if(password !== confirmPassword && !error.length) {
            setUserDetails({ ...userDetails, error: "Passwords do not match.", showError: true });
            return;
        }

        signUpStart({ displayName, email, password });
    }

    const handleChange = (e,type) => {
        setUserDetails({ ...userDetails, [type]: e.target.value, error: '', showError: false })
    }



    return (
        <div className={ 'sign-up' } >
            <h2 className={ 'title' }>I do not have a account</h2>
            <span>Sign up with your e-mail and password</span>
            <form className={ 'sign-up-form' } onSubmit={ handleSubmit }>
                <FormInput
                    type='text'
                    name='displayName'
                    value={ displayName }
                    onChange={ (e) => handleChange(e,'displayName') }
                    label='Display Name'
                    required
                />
                <FormInput
                    type='email'
                    name='email'
                    value={ email }
                    onChange={ (e) => handleChange(e,'email') }
                    label='E-mail'
                    required
                />
                <FormInput
                    type='password'
                    name='password'
                    value={ password }
                    onChange={ (e) => handleChange(e,'password') }
                    label='Password'
                    required
                />
                <FormInput
                    type='password'
                    name='confirmPassword'
                    value={ confirmPassword }
                    onChange={ (e) => handleChange(e,'confirmPassword') }
                    label='Confirm Password'
                    required
                />
                <CustomButton type='submit'>SIGN UP</CustomButton>
            </form>

            {
                error &&
                    <SweetAlert
                        show={ showError }
                        type='warning'
                        title='Something went wrong!'
                        text={ error }
                        onConfirm={ () => setUserDetails({ ...userDetails, error: '', showError: false }) }
                    />
            }
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    userError: selectedUserError,
})

const mapDispatchToProps = dispatch => ({
    signUpStart: userCredentials => dispatch(signUpStart(userCredentials)),
});

export default connect(mapStateToProps,mapDispatchToProps)(SignUp);
