import CartActionTypes from './cart.types';

const INITIAL_STATE = {
    hidden: true,
}

const cartReducer = (state = INITIAL_STATE, action) => {
    console.log(action);
    switch (action.type) {
        case CartActionTypes.TOOGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden,
            }
        default:
            return state;
    }
}

export default cartReducer;
